curr_obj readSRAMSliced(curr_obj currObj, u8 **Read_out_image, u8 ***Read_out_weight, u8 ****Read_out_reference, u8 **Read_out_referenceAdderTree )
{
	//Read_out_image->8x1024
	//Read_out_weight->68x1024x4
	//Read_out_reference=>2x4x8x1024
	//Read_out_referenceAdderTree=>1024x4

//	memset(Read_out_weight	 			,2,68*1024*4*sizeof(u8));
//	memset(Read_out_reference			,2,2*8*1024*4*sizeof(u8));
//	memset(Read_out_referenceAdderTree	,2,1024*4*sizeof(u8));
//	memset(Read_out_image				,2,8*1024*sizeof(u8));

	int numDLs=97;
	int numWWLs=1092;
	int numBLs=4;
	//u8 readSRAMTotal[numDLs][numWWLs][numBLs];
	// Declaring Memory for readSRAMTotal
	// Read_out_weight->97 x 1092 x 4
	u8 ***readSRAMTotal = (u8***)calloc(numDLs,sizeof(u8**));
	for(uint16_t wd = 0; wd < numDLs; wd++){
		readSRAMTotal[wd] = (u8**)calloc(numWWLs,sizeof(u8*));
		for(uint16_t wl = 0; wl < numWWLs; wl++){
			readSRAMTotal[wd][wl] = (u8*)calloc(numBLs,sizeof(u8));
		}
	}

	currObj=readSRAMAll(currObj, readSRAMTotal);


	int bitLine,WWL,DL,LH,otherIMGDLs;

	//Weight Read Out
		for(bitLine=0;bitLine<4;bitLine++)
		{
			for(WWL=0;WWL<1024;WWL++)
			{
				for(DL=0;DL<68;DL++)
				{
					Read_out_weight[DL][WWL][bitLine]=readSRAMTotal[DL_nums_total[map_to_72[DL]]][WWL][bitLine];
				}
			}
		}


	//Reference Read Out
		for(bitLine=0;bitLine<4;bitLine++)
		{
			for(WWL=0;WWL<1024;WWL++)
			{
				for(LH=0;LH<2;LH++)
				{
					for(DL=0;DL<8;DL++)
					{
						Read_out_reference[LH][bitLine][DL][WWL]=readSRAMTotal[DLs_ref[LH][DL]][WWL][bitLine];
					}
				}
			}
		}


		//Reference AdderTree Read Out
		for(bitLine=0;bitLine<4;bitLine++)
		{
			for(WWL=0;WWL<1024;WWL++)
			{
					Read_out_referenceAdderTree[WWL][bitLine]=readSRAMTotal[0][WWL][bitLine];
			}
		}

		int sum_otherIMGDLs;
		//Read_out_image Read Out
		for(WWL=0;WWL<1024;WWL++)
		{
			for(DL=0;DL<2;DL++)
				{
				for(bitLine=0;bitLine<4;bitLine++)
						{

							if((readSRAMTotal[IMG_DL_nums[DL]][WWL][bitLine]))//Predicted IMAGE=1
							{
								sum_otherIMGDLs=0;
								for(otherIMGDLs=0;otherIMGDLs<3;otherIMGDLs++)//0 1 2 => DL=0 :=  0+2*1   0+2*2   0+2*3 => IMG_DL_nums_needed={3,5,7} ;;;  DL=1 :=  1+2*1   1+2*2   1+2*3 => IMG_DL_nums_needed={4,6,8} //u8 IMG_DL_nums[8]={1,2,3,4,5,6,7,8};
								{

									if(!(readSRAMTotal[IMG_DL_nums[DL+2*(otherIMGDLs+1)]][WWL][bitLine]))
										{
											sum_otherIMGDLs++;
										}
								}
								if(sum_otherIMGDLs==3)
								{
									Read_out_image[DL*4+bitLine][WWL]=readSRAMTotal[IMG_DL_nums[DL]][WWL][bitLine];//ACTUAL IMAGE=1 Verified
								}
								else
								{
									Read_out_image[DL*4+bitLine][WWL]=2;//2    :====   MSB=1 but other bits are not all zeroes
								}
							}

							else//Predicted IMAGE=0
							{
								sum_otherIMGDLs=0;
								for(otherIMGDLs=0;otherIMGDLs<3;otherIMGDLs++)//0 1 2 => DL=0 :=  0+2*1   0+2*2   0+2*3 => IMG_DL_nums={3,5,7} ;;;  DL=1 :=  1+2*1   1+2*2   1+2*3 => IMG_DL_nums={4,6,8} //u8 IMG_DL_nums[8]={1,2,3,4,5,6,7,8};
								{

									if(!(readSRAMTotal[IMG_DL_nums[DL+2*(otherIMGDLs+1)]][WWL][bitLine]))
										{
											sum_otherIMGDLs++;
										}
								}
								if(sum_otherIMGDLs==2)
								{
									otherIMGDLs=2;
									if(readSRAMTotal[IMG_DL_nums[DL+2*(otherIMGDLs+1)]][WWL][bitLine])
									{
										Read_out_image[DL*4+bitLine][WWL]=readSRAMTotal[IMG_DL_nums[DL]][WWL][bitLine];
									}
									else
									{
										Read_out_image[DL*4+bitLine][WWL]=3;//MSB=0; LSB~=0
									}
								}
								else
								{
									Read_out_image[DL*4+bitLine][WWL]=4;//MSB=0; 2 zeros with MSB-1, LSB-1 Failed
								}
							}


						}
				}
		}

		return currObj;
}
